<?php

namespace AppBundle\Repository;

use AppBundle\Entity\FluxFormationType;
use Doctrine\ORM\EntityRepository;

/**
 * FluxFormationTypeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class FluxFormationTypeRepository extends EntityRepository
{
    public function findOneOrCreate(array $name)
    {
        $entity = $this->findOneBy($name);

        if (null === $entity)
        {
            $entity = new FluxFormationType();
            $entity->setName($name['name']);
            $this->_em->persist($entity);
            $this->_em->flush();
        }

        return $entity;
    }

    public function getNotComplete()
    {
        $entities = $this->createQueryBuilder('fft')
                        ->where('fft.formationType IS NULL')
                        ->getQuery()
                        ->getResult();

        return $entities;
    }
}
