<?php

namespace AppBundle\Repository;

use AppBundle\Entity\FluxJobField;
use Doctrine\ORM\EntityRepository;

/**
 * FluxJobFieldRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class FluxJobFieldRepository extends EntityRepository
{
    public function findOneOrCreate(array $name)
    {
        $entity = $this->findOneBy($name);

        if (null === $entity)
        {
            $entity = new FluxJobField();
            $entity->setName($name['name']);
            $this->_em->persist($entity);
            $this->_em->flush();
        }

        return $entity;
    }

    public function getNotComplete()
    {
        $entities = $this->createQueryBuilder('fjf')
                        ->where('fjf.jobField IS NULL')
                        ->getQuery()
                        ->getResult();

        return $entities;
    }
}
